pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

contract ToDoList {
    struct Task {
        uint256 id;
        uint256 date;
        string content;
        string author;
        bool done;
    }

    Task[] public tasks;

    function createTask(string calldata _content, string calldata _author)
        external
    {
        tasks.push(Task(tasks.length, now, _content, _author, false));
    }

    function getTask(uint256 _id) external view returns (Task memory) {
        return tasks[_id];
    }

    function getAllTask() external view returns (Task[] memory) {
        return tasks;
    }
}
